import { injected } from './wallet/connector'
import { useWeb3React } from '@web3-react/core'
import Web3 from 'web3'
const web3 = new Web3("http://127.0.0.1:8545")

function Navigation(){
     const connectWallet = async () => {
        if (window.ethereum) { //check if Metamask is installed
              try {
                  const address = await window.ethereum.enable(); //connect Metamask
                  const obj = {
                          connectedStatus: true,
                          status: "",
                          address: address
                      }
                      return obj;

              } catch (error) {
                  return {
                      connectedStatus: false,
                      status: "🦊 Connect to Metamask using the button on the top right."
                  }
              }

        } else {
              return {
                  connectedStatus: false,
                  status: "🦊 You must install Metamask into your browser: https://metamask.io/download.html"
              }
            } 
      };
    function shortAccount(address){
        return address.substring(0, 6) + "..." + address.substring(38, 42);
    }

    return(
    <nav className='navbar'>
        <div className='navbar__title navbar__item'>Home</div>
        <div className='navbar__item'>Gallery</div>
        <div className='navbar__item'>Marketplace</div>
        <div className='navbar__item'>Rarity check</div>

        <div className='navbar__item'>
            <button onClick={connectWallet}>
                 <span>{shortAccount(address)}</span> <span>Connect to metamask</span>
            </button>
        </div>        
    </nav>
    );
}

export default Navigation;
