import React, { useEffect } from 'react';
import abiDecoder from 'abi-decoder'
import ReactDOM from 'react-dom'
import Web3 from 'web3'
import reactDom from 'react-dom';
const IPFS = require('ipfs-http-client');

//Contract declaration and init
const contract_abi = require('./abi.json'); //To change during deployment
const web3 = new Web3("https://rinkeby.infura.io/v3/9aa3d95b3bc440fa88ea12eaa4456161"); //To change during deployment
const contractAddr = "0x1130753c69717efa1aa8450005e026788810bcd6"; //To change during deployment

function Gallery(){
    var tokenURI = 'a';
    const url = "https://cdn.filestackcontent.com/XYrHCaFGRSaq0EPKY1S6";

    abiDecoder.addABI(contract_abi);
    var contract = new web3.eth.Contract(
        contract_abi,       
        web3.utils.toChecksumAddress(contractAddr)
    );

    function renderImage(tokenURI) {
        const images = ( 
            <div class="container-images">
                <img src={tokenURI}/>
                <p>
                    {tokenURI}
                </p>
            </div>
        );
        ReactDOM.render(
            images,
            document.getElementById('galleryId')
        );
    }

    async function load() {
        const node = await IPFS.create();
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        console.log(accounts[0])
        let walletNfts = await contract.methods.walletOfOwner(accounts[0]).call();
        console.log(walletNfts);

        tokenURI = await contract.methods.tokenURI("1").call();
        console.log(tokenURI);
        renderImage(tokenURI)

        //testing ipfs
        let bufs = []
        for await (const buf of node.cat(tokenURI)) {
        bufs.push(buf)
        }
        const data = Buffer.concat(bufs);
        console.log(data);
    }
    return(
        <div class="Gallery" >
            <h1>Gallery</h1>
            <div id="galleryId">
                <button onClick={load}>
                    Press me
                </button>
            </div>
        </div>
    );
}
export default Gallery;
