function Navigation(){
    async function connect_mm(){
        if (window.ethereum) { //check if Metamask is installed
            try {
                const address = await window.ethereum.enable(); //connect Metamask
                const obj = {
                        connectedStatus: true,
                        status: "",
                        address: address
                    }
                    return obj;

            } catch (error) {
                return {
                    connectedStatus: false,
                    status: "🦊 Connect to Metamask using the button on the top right."
                }
            }

      } else {
            return {
                connectedStatus: false,
                status: "🦊 You must install Metamask into your browser: https://metamask.io/download.html"
            }
          } 
    }
    return(
    <nav className='navbar'>
        <div className='navbar__title navbar__item'>Home</div>
        <div className='navbar__item'>Gallery</div>
        <div className='navbar__item'>Marketplace</div>
        <div className='navbar__item'>Rarity check</div>

        <div className='navbar__item' onClick={connect_mm()}>Connect To Metamask</div>        
    </nav>
    );
}

export default Navigation;
