import Web3 from 'web3'
import abiDecoder from 'abi-decoder'
import { useState } from 'react';
import React from 'react';
const BigNumber = require("bignumber.js");


//Contract declaration and init
const contract_abi = require('./abi.json'); //To change during deployment
const web3 = new Web3("http://127.0.0.1:8545"); //To change during deployment
const contractAddr = "0xcA03e5302148115599658A26eeac9E253B1F1e9e"; //To change during deployment


function Mint(){

    abiDecoder.addABI(contract_abi);
    var contract = new web3.eth.Contract(
        contract_abi,       
        web3.utils.toChecksumAddress(contractAddr)
    );
    const [input, setInput] = useState(1);

    async function sendMint(){

        console.log("It's working! ");
        console.log("Input = " + input);
        const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
        console.log(accounts[0]);

        const mint = await contract.methods.mint(
            BigNumber(input)
        );

        console.log(mint);
        var encodedABI = mint.encodeABI();

        console.log("Account: " + accounts[0]);
        console.log("gas: 141179");
        console.log("Data: " + encodedABI);
        var tx = {
            from: accounts[0],
            to: web3.utils.toChecksumAddress(contractAddr),
            //gas: "141179",
            data: encodedABI,
            //gasPrice: web3.utils.toWei("10", 'Gwei'),
            value: web3.utils.toHex(80000000000000000)
        }
        const txHash = await window.ethereum.request({
            method: 'eth_sendTransaction',
            params: [tx],
          });
    }

    return(
        <div class="centered">
            <img src={process.env.PUBLIC_URL + 'Matlab_logo.png'} style={{width: 250}}  />
            <p>
            Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book. It has survived not only five centuries, but also the leap into electronic typesetting, remaining essentially unchanged. It was popularised in the 1960s with the release of Letraset sheets containing Lorem Ipsum passages, and more recently with desktop publishing software like Aldus PageMaker including versions of Lorem Ipsum.
            </p>
            <form>
                <input type="number" value={input} onInput={e => setInput(e.target.value)} placeholder="1" min="1" max="25" style={{padding: "10px", }}></input>
                <input type="button" value="Mint" name="Mint" text="mint" class="mint-button" onClick={sendMint}>

                </input>
            </form>
        </div>

    );
}

export default Mint;
