import { injected } from './wallet/connector'
import { useWeb3React } from '@web3-react/core'
import Web3 from 'web3'
const web3 = new Web3("http://127.0.0.1:8545")

function Navigation(){
    const active = false;
    var displayText = "Connect to metamask";
    // async function connect() {
    //     try {
    //         await activate(injected)
    //     } catch (ex) {
    //     console.log(ex)
    //     }
    // }
    const onClickConnect = async () => {
        try {
          await window.ethereum.request({ method: 'eth_requestAccounts' });
          active = true;
        } catch (error) {
          console.error(error);
        }
    };
    function shortAccount(account){
        return account.substring(0, 6) + "..." + account.substring(38, 42);
    }

    return(
    <nav className='navbar'>
        <div className='navbar__title navbar__item'>Home</div>
        <div className='navbar__item'>Gallery</div>
        <div className='navbar__item'>Marketplace</div>
        <div className='navbar__item'>Rarity check</div>

        <div className='navbar__item'>
            <button onClick={onClickConnect}>
                {active ? <span>{shortAccount(window.ethereum.request({ method: 'eth_accounts' }))}</span> : <span>Connect to metamask</span>}
            </button>
        </div>        
    </nav>
    );
}

export default Navigation;
